bool ImageFormat::hasSwizzle()
{
    bool has_swizzles = false;

#ifdef OSG_OGL_TEXTURE_SWIZZLE
    Int32 extIdx = Window::getExtensionIdX("GL_ARB_texture_swizzle");
    bool  hasExt = Window::hasCommonExtension(extIdx);
#endif

    return has_swizzles;
}

