ImageFormat::Format ImageFormat::find(GlInternalFormat internalFormat, GlExternalFormat externalFormat, GlTypeFormat type)
{
    std::size_t sz = _formatDesc.size();

    for (std::size_t idx = 0; idx <= sz; ++idx)
    {
        if (_formatDesc[idx].internalFormat != internalFormat) continue;
        if (_formatDesc[idx].externalFormat != externalFormat) continue;
        if (_formatDesc[idx].type           != type)           continue;

        return static_cast<Format>(idx);
    }

    return FORMAT_UNDEFINED;
}

